import React,{Component} from 'react';
import {Navbar,Nav,Container} from 'react-bootstrap';
import {Link} from 'react-router-dom';

class navigation extends Component{
    constructor(){
        super()
        this.state={
            activeIndex:0,
        }
    }

    addCssActive(idx){
        this.setState({activeIndex:idx})        
    }
    componentDidMount(){
        let url = window.location.pathname

        if(url ==='/signin'){
            this.setState({activeIndex:2})
        }else{
            this.setState({activeIndex:3})
        }
    }
    render(){
        console.log(this.state.activeIndex)
        return(
            <Navbar collapseOnSelect expand="lg">
                <Container>
                    <Navbar.Brand href="#home">USU CC</Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav className="mr-auto">

                        </Nav>
                        <Nav>
                        <Nav>
                            <Link className="nav-link" to="/jobseeker">Jobseeker</Link>
                        </Nav>
                        <Nav>
                            <Link className="nav-link" to="/employeer">Employeer</Link>
                        </Nav>
                        <Nav>
                            <Link className="nav-link" to="/student" >Student</Link>
                        </Nav>
                        {/* <NavDropdown title="Dropdown" id="collasible-nav-dropdown">
                            <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                            <NavDropdown.Divider />
                            <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                        </NavDropdown> */}
                        <Nav>
                            <Link className="nav-link" to="/signup">Sign Up</Link>
                        </Nav>
                        <Nav>
                            <Link className="nav-link" to="/signin" onClick={()=>this.addCssActive(2)}>
                            Sign In
                            </Link>
                        </Nav>
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
        )
    }
}

export default navigation;
